import React from 'react';
import './category.css';
import FormInput from './forminput';

class Index extends React.Component{
    constructor(){
        super();
        this.state={
            ListData :
            [
                {id : 1, initial : 'FSH', name: 'Fashion', isActive:true},
                {id : 2, initial : 'FOO', name: 'Food', isActive:true},
                {id : 3, initial : 'ELC', name: 'Electronic', isActive:true},
                {id : 4, initial : 'FUR', name: 'Furniture', isActive:true},
                {id : 5, initial : 'DRN', name: 'Drink', isActive:true}
            ],
            isShow:false
        }
    }
    handlerCreate = () =>{
        this.setState({
            isShow:true
        })
    }
    handlerCancel = () =>{
        this.setState({
            isShow:false
        })
    }
    functionChange = name => ({target:{value}}) => {
        this.setState({
            [name] :value
        })
    }

    render(){
        return (
            <div>
                <p>Data Category</p>

                {this.state.isShow ? <FormInput handlerCancel={this.handlerCancel} /> : null} <br />
                
                <button onClick={this.handlerCreate}>Create New</button>
                <table id="category" >
                    <tr>
                        <th>Id</th>
                        <th>Initial</th>
                        <th>Name</th>
                        <th>isActive</th>
                        <th>Action</th>
                    </tr>
                    {
                        this.state.ListData.map(data=> {
                            return(
                                <tr>
                                    <td>{data.id}</td>
                                    <td>{data.initial}</td>
                                    <td>{data.name}</td>
                                    <td>{data.isActive.toString()}</td>
                                    <td><button>Edit</button>
                                        <button>Delete</button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </table>
            </div>
        )
    }
}

export default Index
