import React from 'react';
import Increment from './Increment';
import Plus from './Plus';

class App extends React.Component{
    constructor(){
        super();
        this.state ={
            nilai1 : 1,
            nilai2 : 2,
            hasil :0
        }
    }

    functionIncrement= ()=> {
        
        this.setState({
            nilai1: this.state.nilai1+1
        })
    }
    functionPlus = () => {
        this.setState({
            hasil : this.state.nilai1 + this.state.nilai2
        })
    }
    render(){
        return(
            <div>
                Welcome to React
                <p>{this.state.nilai1}</p>
               <p>{this.state.nilai2}</p>
               <Increment functionIncrement={this.functionIncrement} /> 

               Nilai 1 : <input type="number" />
               Nilai 2 : <input type="number"/>

               <Plus functionPlus={this.functionPlus} hasil={this.state.hasil} />
            </div>
        )
    }
}
export default App;
